import { useForm } from 'react-hook-form';
import { account } from '@/lib/appwrite';
import { useRouter } from 'next/router';
import { Button, Input, Card } from '@/components/ui';

export default function Login() {
  const { register, handleSubmit } = useForm();
  const router = useRouter();

  const onSubmit = async (data) => {
    try {
      await account.createEmailSession(data.email, data.password);
      router.push('/app/(app)/account');
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Card className="p-8 max-w-md mx-auto">
      <h1 className="text-2xl font-bold mb-4">Login</h1>
      <form onSubmit={handleSubmit(onSubmit)} className="flex flex-col gap-4">
        <Input {...register('email')} type="email" placeholder="Email" />
        <Input {...register('password')} type="password" placeholder="Password" />
        <Button type="submit">Login</Button>
      </form>
    </Card>
  );
}

import { useRouter } from 'next/router';
import { useEffect, useState } from 'react';
import { account } from '@/lib/appwrite';
import { Button, Card } from '@/components/ui';

export default function Account() {
  const [user, setUser] = useState(null);
  const router = useRouter();

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const user = await account.get();
        setUser(user);
      } catch (err) {
        router.push('/app/(app)/login');
      }
    };
    checkAuth();
  }, []);

  return (
    <Card className="p-8 max-w-md mx-auto">
      <h1 className="text-2xl font-bold mb-4">Account Page</h1>
      {user ? <p>Welcome, {user.email}!</p> : <p>Loading...</p>}
      <Button onClick={() => router.push('/app/(app)/login')}>Logout</Button>
    </Card>
  );
}

import { NextResponse } from 'next/server';
import { account } from '@/lib/appwrite';

export async function middleware(req) {
  try {
    await account.get();
    return NextResponse.next();
  } catch (err) {
    return NextResponse.redirect(new URL('/app/(app)/login', req.url));
  }
}

export const config = {
  matcher: ['/app/(app)/account/:path*'],
};

import { Client, Account } from '@appwrite/web';

const client = new Client();
client.setEndpoint('your-appwrite-endpoint').setProject('your-appwrite-project-id');

const account = new Account(client);

export { client, account };
